import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody } from "reactstrap";
// import AppModal from "../Modal/Modal";

import "./styles.scss";

const CardContainer = (props) => {
  const { title, body, viewAttachment } = props;
  const [isCollapseOpen, setIsCollapseOpen] = useState(false);
  const [isAcceptModalOpen, setIsAcceptModalOpen] = useState(false);
  const [isRejectModalOpen, setIsRejectModalOpen] = useState(false);

  const acceptHandler = () => {
    console.log("clicked");
    setIsAcceptModalOpen(true);
  };

  const toggle = () => setIsAcceptModalOpen((prev) => !prev);

  return (
    <div className="card-container">
      <div className="left">
        <div className="title">{title}</div>
        <div className="body">{body}</div>
        <div className="footer">
          <button
            type="link"
            className="btn btn-collapse"
            onClick={() => {
              setIsCollapseOpen((prev) => !prev);
            }}
          >
            View Details
          </button>
          {viewAttachment && (
            <button className="btn btn-primary" style={{ marginLeft: "10px" }}>
              View Attachment
            </button>
          )}
          <div className={`collapse-body ${isCollapseOpen ? "show" : "hide"}`}>
            Find details here...
          </div>
        </div>
      </div>
      <div className="right">
        <div>
          <button className="btn btn-active" onClick={acceptHandler}>
            ACCEPT
          </button>
          <Modal
            isOpen={isAcceptModalOpen}
            toggle={toggle}
            className="modal-container"
          >
            <ModalHeader toggle={toggle}>Note</ModalHeader>
            <ModalBody>Your request has been accepted</ModalBody>
          </Modal>
        </div>
        <div>
          <button className="btn btn-secondary">REJECT</button>
        </div>
      </div>
    </div>
  );
};

export default CardContainer;
